CREATE DATABASE foodreviewdb;

USE foodreviewdb;

CREATE TABLE foods (
	id VARCHAR(64),
	name VARCHAR(30),
	description TEXT,
	date_added DATETIME,
	PRIMARY KEY (id)
);

CREATE USER 'foodusr' IDENTIFIED BY 'foodpwd';

GRANT ALL ON *.* TO 'foodusr'@'localhost' IDENTIFIED BY 'foodpwd';

